var play = {
    /// <summary>
    ///  版本
    /// </summary>
    version: "5.0.2.1",
    docs: '未经允许请勿修改',
    sdk: {
        init: function (_init, func) {
            play.init(_init, func);
        },
        pay: function (_pay) {
            play.pay(_pay);
        },
        dev: function (_dev, func) {
            play.dev(_dev, func);
        },
        role: function (_role, func) {
            play.role(_role, func);
        },
        server: function (_server, func) {
            play.selectserver(_server, func);
        }
    },
    data: {
        init: "init", pay: "pay", dev: "dev", role: "role", server: "server",
    }
}
/*
<!--game_files_start-->
*/
play.init = function (_init, func) {
    _init.action = play.data.init;
    window.parent.postMessage(JSON.stringify(_init), "*");
    play.protocol(func);
}
/*
<!--game_pay_start-->
*/
play.pay = function (_paydata) {
    _paydata.action = play.data.pay;
    var payStr = JSON.stringify(_paydata);
    window.parent.postMessage(payStr, "*");
}
/*
<!--game_dev_start-->
*/
play.dev = function (_dev, func) {
    _dev.action = play.data.dev;
    var dev = JSON.stringify(_dev);
    window.parent.postMessage(dev, "*");
    play.protocol(func);
}
/*
<!--game_role_start-->
*/
play.role = function (_role, func) {
    _role.action = play.data.role;
    var r = JSON.stringify(_role);
    window.parent.postMessage(r, "*");
    play.protocol(func);
}
/*
<!--game_server_start-->
*/
play.selectserver = function (_server, func) {
    _server.action = play.data.server;
    var r = JSON.stringify(_server);
    window.parent.postMessage(r, "*");
    play.protocol(func);
}
play.protocol = function (func) { window.addEventListener("message", function (evt) { if (evt.source != window.parent) return; if (typeof func == 'function') { func(evt.data); } }, false); }


